mapper.select:
		select `equipment_id`,`equipment_power`,`equipment_name`,`equipment_code`,`enterprise_id`,`phase_number`,`number`,`is_or_not_through`,`install_position`,`equipment_use`,`product_id`,`protection_person`,`phone_number`,`equipment_type`,`surveyted_person_name`,`surveyted_person_position`,`full_form_time`,`survey_person_name`,`create_time`,`create_user_name`,`create_by`,`modify_time`,`modify_user_name`,`modify_by`,`delete_flag`,`administrative_division`,`urbanorrural`,`country` from eco_electric

			delete_flag = '0'
		  		   and equipment_id = #{equipmentId} 
		  		   and equipment_name = #{equipmentName} 
		  		   and equipment_code = #{equipmentCode} 
		  		   and enterprise_id = #{enterpriseId} 
		  		   and phase_number = #{phaseNumber} 
		  		   and number = #{number} 
		  		   and is_or_not_through = #{isOrNotThrough} 
		  		   and install_position = #{installPosition} 
		  		   and equipment_use = #{equipmentUse} 
		  		   and product_id = #{productId} 
		  		   and protection_person = #{protectionPerson} 
		  		   and phone_number = #{phoneNumber} 
		  		   and equipment_type = #{equipmentType} 
		  		   and surveyted_person_name = #{surveytedPersonName} 
		  		   and surveyted_person_position = #{surveytedPersonPosition} 
		  		   and full_form_time = #{fullFormTime} 
		  		   and survey_person_name = #{surveyPersonName} 
		  		   and create_time = #{createTime} 
		  		   and create_user_name = #{createUserName} 
		  		   and create_by = #{createBy} 
		  		   and modify_time = #{modifyTime} 
		  		   and modify_user_name = #{modifyUserName} 
		  		   and modify_by = #{modifyBy} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and administrative_division = #{administrativeDivision} 
		  		   and urbanorrural = #{urbanorrural} 
				   and country = #{country} 



                order by ${sort} ${order}


                order by equipment_id desc



			limit #{offset}, #{limit}

