mapper.select:
		select ep.envir_protection_id,ep.enterprise_id,ep.eco_estimate_flg,ep.eco_licence,
		ep.park_flg,ep.eco__standard_flg,ep.industry_code,ep.project_manage_code,ep.is_or_not_acceptance,
		ep.tradable_permits_code,ep.pollution_license_flg,ep.pollution_category_code,ep.annual_inspection_flg,
		ep.area_code,ep.main_energy_code,ep.measures,ep.normal_factors_code,ep.special_factors_code,ep.nomal_waste,
		ep.danger_waste,ep.surveyted_person_name,ep.surveyted_person_position,ep.full_form_time,ep.survey_person_name,
		ep.create_time,ep.create_user_name,ep.create_by,ep.modify_time,ep.modify_user_name,ep.modify_by,ep.delete_flag,ep.administrative_division,ep.dosage,
		ep.urbanorrural,ep.country,en.enterprise_name,ep.other,ep.industry_code,

		(select co.name  from eco_code co where co.code_id = ep.project_manage_code) as projectManageName
		from eco_envprotection ep
		left join eco_enterprise en on en.enterprise_id = ep.enterprise_id

			ep.delete_flag = '0'
		  		   and ep.envir_protection_id = #{envirProtectionId} 
		  		   and ep.enterprise_id = #{enterpriseId} 
		  		   and ep.eco_estimate_flg = #{ecoEstimateFlg} 
		  		   and ep.eco_licence = #{ecoLicence} 
		  		   and ep.park_flg = #{parkFlg} 
		  		   and ep.eco__standard_flg = #{ecoStandardFlg} 
		  		   and ep.industry_code = #{industryCode} 
		  		   and ep.project_manage_code = #{projectManageCode} 
		  		   and ep.is_or_not_acceptance = #{isOrNotAcceptance} 
		  		   and ep.tradable_permits_code = #{tradablePermitsCode} 
		  		   and ep.pollution_license_flg = #{pollutionLicenseFlg} 
		  		   and ep.pollution_category_code = #{pollutionCategoryCode} 
		  		   and ep.annual_inspection_flg = #{annualInspectionFlg} 
		  		   and ep.area_code = #{areaCode} 
		  		   and ep.main_energy_code = #{mainEnergyCode} 
		  		   and ep.measures = #{measures} 
		  		   and ep.normal_factors_code = #{normalFactorsCode} 
		  		   and ep.special_factors_code = #{specialFactorsCode} 
		  		   and ep.nomal_waste = #{nomalWaste} 
		  		   and ep.danger_waste = #{dangerWaste} 
		  		   and ep.surveyted_person_name = #{surveytedPersonName} 
		  		   and ep.surveyted_person_position = #{surveytedPersonPosition} 
		  		   and ep.full_form_time = #{fullFormTime} 
		  		   and ep.survey_person_name = #{surveyPersonName} 
		  		   and ep.create_time = #{createTime} 
		  		   and ep.create_user_name = #{createUserName} 
		  		   and ep.create_by = #{createBy} 
		  		   and ep.modify_time = #{modifyTime} 
		  		   and ep.modify_user_name = #{modifyUserName} 
		  		   and ep.modify_by = #{modifyBy} 
		  		   and ep.administrative_division = #{administrativeDivision} 
		  		   and ep.urbanorrural = #{urbanorrural} 
				   and ep.country = #{country} 



                order by ${sort} ${order}


                order by ep.envir_protection_id desc



			limit #{offset}, #{limit}

select count(*) from eco_envprotection

	 delete_flag = '0'
  		   and envir_protection_id = #{envirProtectionId} 
  		   and enterprise_id = #{enterpriseId} 
  		   and eco_estimate_flg = #{ecoEstimateFlg} 
  		   and eco_licence = #{ecoLicence} 
  		   and park_flg = #{parkFlg} 
  		   and eco__standard_flg = #{ecoStandardFlg} 
  		   and industry_code = #{industryCode} 
  		   and project_manage_code = #{projectManageCode} 
  		   and is_or_not_acceptance = #{isOrNotAcceptance} 
  		   and tradable_permits_code = #{tradablePermitsCode} 
  		   and pollution_license_flg = #{pollutionLicenseFlg} 
  		   and pollution_category_code = #{pollutionCategoryCode} 
  		   and annual_inspection_flg = #{annualInspectionFlg} 
  		   and area_code = #{areaCode} 
  		   and main_energy_code = #{mainEnergyCode} 
  		   and measures = #{measures} 
  		   and normal_factors_code = #{normalFactorsCode} 
  		   and special_factors_code = #{specialFactorsCode} 
  		   and nomal_waste = #{nomalWaste} 
  		   and danger_waste = #{dangerWaste} 
  		   and surveyted_person_name = #{surveytedPersonName} 
  		   and surveyted_person_position = #{surveytedPersonPosition} 
  		   and full_form_time = #{fullFormTime} 
  		   and survey_person_name = #{surveyPersonName} 
  		   and create_time = #{createTime} 
  		   and create_user_name = #{createUserName} 
  		   and create_by = #{createBy} 
  		   and modify_time = #{modifyTime} 
  		   and modify_user_name = #{modifyUserName} 
  		   and modify_by = #{modifyBy} 
  		   and delete_flag = #{deleteFlag} 
  		   and administrative_division = #{administrativeDivision} 
  		   and urbanorrural = #{urbanorrural} 
	 	   and country = #{country}
