mapper.select:
		select fi.equipment_id,fi.equipment_name,fi.equipment_code,fi.enterprise_id,fi.model,fi.buy_time,fi.effect_time,
		fi.brand,fi.install_position,fi.product_id,fi.protection_person,fi.phone_number,fi.surveyted_person_name,fi.surveyted_person_position,
		fi.full_form_time,fi.survey_person_name,fi.create_time,fi.create_user_name,fi.create_by,fi.modify_time,
		fi.modify_user_name,fi.modify_by,fi.delete_flag,fi.administrative_division,fi.urbanorrural,
		en.enterprise_name
		from eco_firedevice fi left join eco_enterprise en on en.enterprise_id = fi.enterprise_id

			fi.delete_flag = '0'
				   and fi.equipment_id = #{equipmentId} 
		  		   and fi.equipment_name = #{equipmentName} 
		  		   and fi.equipment_code = #{equipmentCode} 
		  		   and fi.enterprise_id = #{enterpriseId} 
		  		   and fi.model = #{model} 
		  		   and fi.buy_time = #{buyTime} 
		  		   and fi.effect_time = #{effectTime} 
		  		   and fi.brand = #{brand} 
		  		   and fi.install_position = #{installPosition} 
		  		   and fi.product_id = #{productId} 
		  		   and fi.protection_person = #{protectionPerson} 
		  		   and fi.phone_number = #{phoneNumber} 
		  		   and fi.surveyted_person_name = #{surveytedPersonName} 
		  		   and fi.surveyted_person_position = #{surveytedPersonPosition} 
		  		   and fi.full_form_time = #{fullFormTime} 
		  		   and fi.survey_person_name = #{surveyPersonName} 
		  		   and fi.create_time = #{createTime} 
		  		   and fi.create_user_name = #{createUserName} 
		  		   and fi.create_by = #{createBy} 
		  		   and fi.modify_time = #{modifyTime} 
		  		   and fi.modify_user_name = #{modifyUserName} 
		  		   and fi.modify_by = #{modifyBy} 
		  		   and fi.administrative_division = #{administrativeDivision} 
		  		   and fi.urbanorrural = #{urbanorrural} 



                order by ${sort} ${order}


                order by equipment_id desc



			limit #{offset}, #{limit}

select count(*) from eco_firedevice

	 delete_flag = '0'
  		   and equipment_id = #{equipmentId} 
