mapper.select:
		select `product_id`,`enterprise_id`,`enterprise_name`,`prodect_name`,`specifical`,`model`,`produt_process`,`prodect_param`,`min_price`,`max_price`,`device_name`,`month_production`,`function_remark`,`remark`,`surveyted_person_name`,`surveyted_person_position`,`full_form_time`,`survey_person_name`,`create_time`,`create_user_name`,`create_by`,`modify_time`,`modify_user_name`,`modify_by`,`delete_flag`,`administrative_division`,`urbanorrural` from eco_product

		  		   and product_id = #{productId} 
		  		   and enterprise_id = #{enterpriseId} 
		  		   and enterprise_name = #{enterpriseName} 
		  		   and prodect_name = #{prodectName} 
		  		   and specifical = #{specifical} 
		  		   and model = #{model} 
		  		   and produt_process = #{produtProcess} 
		  		   and prodect_param = #{prodectParam} 
		  		   and min_price = #{minPrice} 
		  		   and max_price = #{maxPrice} 
		  		   and device_name = #{deviceName} 
		  		   and month_production = #{monthProduction} 
		  		   and function_remark = #{functionRemark} 
		  		   and remark = #{remark} 
		  		   and surveyted_person_name = #{surveytedPersonName} 
		  		   and surveyted_person_position = #{surveytedPersonPosition} 
		  		   and full_form_time = #{fullFormTime} 
		  		   and survey_person_name = #{surveyPersonName} 
		  		   and create_time = #{createTime} 
		  		   and create_user_name = #{createUserName} 
		  		   and create_by = #{createBy} 
		  		   and modify_time = #{modifyTime} 
		  		   and modify_user_name = #{modifyUserName} 
		  		   and modify_by = #{modifyBy} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and administrative_division = #{administrativeDivision} 
		  		   and urbanorrural = #{urbanorrural} 



                order by ${sort} ${order}


                order by product_id desc



			limit #{offset}, #{limit}

