mapper.select:
		select `safe_trouble_id`,`enterprise_id`,`equipment_id`,`people_find_name`,`find_time`,`level`,`type`,`apart`,`remark`,`rectificat`,`rect_user_name`,`surveyted_person_name`,`surveyted_person_position`,`full_form_time`,`survey_person_name`,`create_time`,`create_user_name`,`create_by`,`modify_time`,`modify_user_name`,`modify_by`,`delete_flag`,`administrative_division`,`urbanorrural` from eco_risk

		  		   and safe_trouble_id = #{safeTroubleId} 
		  		   and enterprise_id = #{enterpriseId} 
