mapper.select:
		select `training_id`,`enterprise_id`,`training_flg`,`la_provide_flg`,`train_name`,`train_type`,`person_number`,`dept_code`,`traning_file_flg`,`phone_number`,`train_time`,`surveyted_person_name`,`surveyted_person_position`,`full_form_time`,`survey_person_name`,`create_time`,`create_user_name`,`create_by`,`modify_time`,`modify_user_name`,`modify_by`,`delete_flag`,`administrative_division`,`urbanorrural` from eco_training

			delete_flag = '0'
		  		   and training_id = #{trainingId} 
		  		   and enterprise_id = #{enterpriseId} 
		  		   and training_flg = #{trainingFlg} 
		  		   and la_provide_flg = #{laProvideFlg} 
		  		   and train_name = #{trainName} 
		  		   and train_type = #{trainType} 
		  		   and person_number = #{personNumber} 
		  		   and dept_code = #{deptCode} 
		  		   and traning_file_flg = #{traningFileFlg} 
		  		   and phone_number = #{phoneNumber} 
		  		   and train_time = #{trainTime} 
		  		   and surveyted_person_name = #{surveytedPersonName} 
		  		   and surveyted_person_position = #{surveytedPersonPosition} 
		  		   and full_form_time = #{fullFormTime} 
		  		   and survey_person_name = #{surveyPersonName} 
		  		   and create_time = #{createTime} 
		  		   and create_user_name = #{createUserName} 
		  		   and create_by = #{createBy} 
		  		   and modify_time = #{modifyTime} 
		  		   and modify_user_name = #{modifyUserName} 
		  		   and modify_by = #{modifyBy} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and administrative_division = #{administrativeDivision} 
		  		   and urbanorrural = #{urbanorrural} 



                order by ${sort} ${order}


                order by training_id desc



			limit #{offset}, #{limit}

